import './TarjetaFavorito.css';

const TarjetaFavorito = (props)=>{
    const {producto, isFav: esFav, alternarFavorito, setCarro, carrito, imgFav, titleFav, precio} = props;

    const eliminarFav = () => {
        alternarFavorito(producto);

        if (esFav){
            setCarro(carrito.filter(item => item.title !== producto.title));
        }
    };

    return(
        <>
            <img src={imgFav} alt={titleFav} className="tarjeta_img" />
            <img
                className='favorito'
                src={esFav ? 'favoritoOn.png' : 'corazon.svg'}
                alt={titleFav}
                onClick={eliminarFav}
            />
            <div className='contenedor_texto'>
                <h2 className='productos_h2'>{titleFav}</h2>
                <p className='precio'>${precio}</p>
            </div>
        </>
    );
}

export default TarjetaFavorito;
