import React from 'react';
import './TarjetaProducto.css';

const TarjetaProducto = (props) => {
    const {key, imgProd, titleProd, precio, condicion, link, clase, carrito, setCarro, producto, isFavorited, onFavoriteToggle, btnCarro } = props;
    const cargarCarro = () => {
        onFavoriteToggle(producto);  // Llama a la función que maneja el estado de favorito

        if (!isFavorited) {
            const datos = {
                thumbnail: producto.thumbnail,
                title: producto.title,
                price: producto.price
            };
            setCarro([...carrito, datos]);
        }
    };

    return (
        <div className={clase} key={key}>
            <>
                <img src={imgProd} alt={titleProd} className="tarjeta_img" />
                <img
                    className='favorito'
                    src={isFavorited ? 'favoritoOn.png' : 'corazon.svg'}
                    alt={titleProd}
                    onClick={cargarCarro}
                />
                <div className='contenedor_texto'>
                    <h2 className='productos_h2'>{titleProd}</h2>
                    <p className='condicion'>Condición: {condicion === "new" ? "Nuevo" : "Usado"}</p>
                    <p className='precio'>${precio}</p>
                    <a href={link} className='producto_link'>Ir a ML ↗</a>
                </div>
            </>


        </div>
    );
};

export default TarjetaProducto;
